#include<iostream>
using namespace std;
int findMissing(int arr[], int n) {
        // sum of elements of given ap according to n and first element
        float l=n;
        float a = (l+1)/2;
        int b = arr[0]+arr[n-1];
        int sum = a*b;
        // sum of elements of given array
        int sum1=0;
        for(int i=0 ; i<n ; i++)
        {
            sum1 = sum1+arr[i];
        }
        int ans = sum - sum1;
        
        return ans ;
    }
